import colors from "picocolors";
import { removeHydrationClientFiles } from "../common/src/template/client.js";
const logErrorAndExit = (error) => {
  logError(error);
  process.exit(1);
};
const logError = (error) => {
  console.error(colors.red(`ERROR: ${error}`));
};
const logWarning = (warning) => {
  console.warn(colors.yellow(`WARNING: ${warning}`));
};
const logErrorAndClean = async (error, projectStructure) => {
  await removeHydrationClientFiles(projectStructure);
  logErrorAndExit(error);
};
export {
  logError,
  logErrorAndClean,
  logErrorAndExit,
  logWarning
};
